    unsigned int loc   = pos - index *32;
    decision ? summary |= (1 << loc) : summary &= ~(1 << loc);
    m_conditionsValueSummaries[key] = summary;









    std::pair<int,unsigned int>  key = std::make_pair(bx,index); 
