    unsigned int index = channel / 32;
    std::pair<int,unsigned int>  key = std::make_pair(bx,index);
    std::map<std::pair<int,unsigned int>,unsigned int>::const_iterator it = m_channelsDecisionSummaries.find( key );
    if( it == m_channelsDecisionSummaries.end())return 0;
    unsigned int summary = (*it).second;
    unsigned int loc   = channel - index *32;
    return ( 1 & (summary >> loc));




    unsigned int index = channel / 32;
    std::pair<int,unsigned int>  key = std::make_pair(bx,index);
    std::map<std::pair<int,unsigned int>,unsigned int>::const_iterator it = m_channelsPreDecisionSummaries.find(key);
    if( it == m_channelsPreDecisionSummaries.end())return 0;
    unsigned int summary = (*it).second;
    unsigned int loc   = channel - index *32;
    return ( 1 & (summary >> loc));





    unsigned int index = condition / 32;
    std::pair<int,unsigned int>  key = std::make_pair(bx,index);
    std::map<std::pair<int,unsigned int>,unsigned int>::const_iterator it = m_conditionsValueSummaries.find(key);
    if( it == m_conditionsValueSummaries.end())return 0;
    unsigned int summary = (*it).second;
    unsigned int loc   = condition - index *32;
    return ( 1 & (summary >> loc));








