    std::string bad = "LHCb::L0DUConfig is missing";
    if(NULL == m_configuration)return bad;
    LHCb::L0DUChannel::Map channels= m_configuration->channels();
    LHCb::L0DUChannel::Map::iterator it;
    for(it = channels.begin() ; it != channels.end() ; it++){
     unsigned int id = ((*it).second)->id();
     if( channel == id ) return ((*it).second)->name();
    }          
    bad = " Channel no " ;
    std::stringstream os(" ");
    os << channel;
    bad += os.str();
    bad += " is not defined in the loaded configuration - Please check " ;
    return bad;          







    std::string bad = "LHCb::L0DUConfig is missing";
    if(NULL == m_configuration)return bad;
    LHCb::L0DUElementaryCondition::Map conditions = m_configuration->conditions();
    LHCb::L0DUElementaryCondition::Map::iterator it;
    for(it = conditions.begin() ; it != conditions.end() ; it++){
     unsigned int id = ((*it).second)->id();
