    if(NULL == m_configuration)return false;
    LHCb::L0DUChannel::Map channels = m_configuration->channels();
    for(LHCb::L0DUChannel::Map::iterator it = channels.begin();it!=channels.end();it++){
     LHCb::L0DUChannel* channel = (*it).second;
     if( channelDecision( channel->id(),bx  ) && channel->enable() )return true;
    }
