    if(NULL == m_configuration)return false;
    LHCb::L0DUTrigger::Map triggers = m_configuration->triggers();
    LHCb::L0DUTrigger::Map::iterator it = triggers.find( name );
    if( it == triggers.end()){
      if( "L0" == name){
        return decisionFromSummary( bx );
      }else{
        return false;
     }
    }

    LHCb::L0DUChannel::Map channels = (*it).second->channels();
    for(LHCb::L0DUChannel::Map::iterator ic = channels.begin();ic != channels.end();++ic){
     if( channelDecisionByName( (*ic).second->name(), bx) )return true;
    }
    return false;





    if(NULL == m_configuration)return false;
    LHCb::L0DUTrigger::Map triggers = m_configuration->triggers();
