package.class:

    &DataObject;




for ( unsigned int jj=0 ; m_algs.size() > jj ; jj++ ) {
  if ( m_algs[jj].first == name ) {
    m_algs[jj].second = status;
    return;
  }
}
m_algs.push_back( std::pair<std::string,int>(name,status) );





for ( unsigned int jj=0 ; m_algs.size() > jj ; jj++ ) {
  if ( m_algs[jj].first == name ) {
    return m_algs[jj].second;
  }
}
return 0;
